    })
  }

  if (!isValidObjectId(placeId)) {
    return res.json({
      message: 'El id del lugar no es valido'
    })
  }
  const placeFound = await Place.findOne({ _id: placeId })
  if (!placeFound) {
    return res.json({
      message: 'No existe ese lugar'
